import React from 'react';
import Modal from './modal';
import Backdrop from './Backdrop';

function Todo({text}) {
    const deleteHandler = () => {
        return(
            <div>
                <Modal />
                <Backdrop/>
            </div>
        // console.log(`${text} Clicked !`)
        )
    }

    return (
        <div className='card'>
            <h2>{text}</h2>
            <div className='actions'>
                <button className='btn' onClick={deleteHandler}>Delete</button>
            </div>
        </div>
    )
}

export default Todo;
